 //**********************************************************************************************************************************************************
 //Indicator
 //**********************************************************************************************************************************************************
 /**
 * Extends mxShape.
 */
 function mxShapePidIndicator(bounds, fill, stroke, strokewidth) {
 mxShape.call(this);
 this.bounds = bounds;
