 c.fillAndStroke();

 c.begin();
 c.moveTo(w * 0.23, h * 0.46);
 c.lineTo(w * 0.23, h);
 c.moveTo(w * 0.46, h * 0.23);
 c.lineTo(w, h * 0.23);
 c.moveTo(w * 0.77, h * 0.15);
 c.lineTo(w * 0.69, h * 0.31);
 c.moveTo(w * 0.82, h * 0.15);
 c.lineTo(w * 0.74, h * 0.31);
 c.stroke();
 };

