 if (mounting === mxShapePidLogic.prototype.cst.ROOM) {
 c.begin();
 c.moveTo(0, h * 0.5);
 c.lineTo(w, h * 0.5);
 c.stroke();
 }
 else if (mounting === mxShapePidLogic.prototype.cst.INACCESSIBLE) {
 c.setDashed(true);
 c.begin();
 c.moveTo(0, h * 0.5);
 c.lineTo(w, h * 0.5);
 c.stroke();
