 visualElement.subElement = {
 x: Math.min(realNozzles[0].x, realNozzles[1].x),
 y: Math.min(realNozzles[0].y, realNozzles[1].y),
 height: realNozzles[0].y < realNozzles[1].y ? realNozzles[1].y - realNozzles[0].y : realNozzles[0].y - realNozzles[1].y,
 width: Math.sqrt((realNozzles[0].x - realNozzles[1].x) ** 2 + (realNozzles[0].y - realNozzles[1].y) ** 2),
 rotation: visualElement.rotation // inherit the rotation from the parent
 };
 // TODO: decide on when the height or width will be 0
 if (visualElement.subElement.height < 1) {
 visualElement.subElement.height = 10;
 }
 if (visualElement.subElement.width < 1) {
 visualElement.subElement.width = 10;
 }
 }
 // we need to now search for the communication
 let communicationElementAttr = file.evaluate(`//InstanceHierarchy[@Name="ModuleTypePackage"]//InternalElement[@Name="Communication"]` +
 `/InternalElement[@Name="InstanceList"]//Attribute[@Name="RefID"][Value="${visualElement.refId}"]`, file, null, XPathResult.ANY_TYPE, null).iterateNext();
 let communicationElement = communicationElementAttr ? communicationElementAttr.parentElement : undefined;
 let communicationObject = undefined;
 if (communicationElement != null) {
 let communicationDescription = communicationElement.querySelector("Description").textContent;
 let interfaceList = [];
 // interate through the attributes and try to get each communication element
 for (let i = 0; i < communicationElement.childElementCount; i++) {
 const attrElement = communicationElement.children[i];
 if (attrElement.nodeName == "Attribute" && attrElement.getAttribute("Node") != "RefID") {
 let attrValue = attrElement.getElementsByTagName("Value")[0].textContent;
 // search for this attribute in the communication lib
 let sourceCommunicationElement = file.querySelector(`InstanceHierarchy[Name="ModuleTypePackage"] InternalElement[Name="Communication"] ` +
 `InternalElement[Name="SourceList"] ExternalInterface[ID="${attrValue}"]`);
 if (sourceCommunicationElement) {
 interfaceList.push({
 access: this.getAttributeTagValue(sourceCommunicationElement, "Access", false),
 identifier: this.getAttributeTagValue(sourceCommunicationElement, "Identifier", false),
 namespace: this.getAttributeTagValue(sourceCommunicationElement, "Namespace", false),
 type: ElementType.COMMUNICATION_INTERFACE,
 id: sourceCommunicationElement.getAttribute("ID"),
 name: attrElement.getAttribute("Name"),
 endpoint: this.getAttributeTagValue(sourceCommunicationElement.parentElement, "Endpoint", false)
 });
 }
 }
 }
 communicationObject = {
 description: communicationDescription,
 interfaces: interfaceList,
 type: ElementType.COMMUNICATION_OBJECT,
 id: communicationElement.getAttribute("ID"),
 name: communicationElement.getAttribute("Name")
 };
 }
 if (communicationObject) {
 diagram.elements.push({ communication: communicationObject, obj: visualElement });
 }
 else {
 diagram.elements.push({ obj: visualElement });
 }
 }
 parseHmiPipe(element, diagram) {
 let pipe = {
 id: element.getAttribute("ID"),
 name: element.getAttribute("Name"),
 type: ElementType.PIPE
 };
 // get the parsed path
 let parsedPath = this.parseEdgePath(this.getAttributeTagValue(element, "Edgepath", false));
 // parse the nozzles
 pipe.nozzles = this.getNozzleList(element);
 // check to see if the the source and target points form the path are equal to a nozzle
 for (const nozzle of pipe.nozzles) {
 if (parsedPath.source.equals(nozzle)) {
 pipe.source = nozzle;
 }
 if (parsedPath.target.equals(nozzle)) {
 pipe.target = nozzle;
 }
 }
 // check if the soruce and target were assigned
 if (!pipe.source) {
 pipe.source = parsedPath.source;
 }
 if (!pipe.target) {
 pipe.target = parsedPath.target;
 }
 pipe.waypoints = parsedPath.waypoints;
 diagram.pipes.push(pipe);
 }
 getNozzleList(element) {
 let nozzles = [];
 for (let j = 0; j < element.childElementCount; j++) {
 const subElement = element.children[j];
 if (subElement.nodeName == "InternalElement") {
 nozzles.push({
 id: subElement.getAttribute("ID"),
 name: subElement.getAttribute("Name"),
 x: this.getAttributeTagValue(subElement, "X", true),
 y: this.getAttributeTagValue(subElement, "Y", true),
 externalConnectorId: element.querySelector('ExternalInterface[Name="Connector"]').getAttribute("ID"),
 baseClass: subElement.getAttribute("RefBaseSystemUnitPath"),
 type: ElementType.NOZZLE
 });
 }
 }
 return nozzles;
 }
 /**
 * Computes a list of points into a start, end and intermidiate points
 */
 parseEdgePath(edgePath) {
 let tokens = edgePath.split(";");
 return {
 source: new Point(tokens[0]),
 target: new Point(tokens[tokens.length - 1]),
 waypoints: tokens.slice(1, -1).map((el) => { return new Point(el); })
 };
 }
 /**
 * XML parsing fucntion, helping getting the Value tag of a Attribute tag identified by the attrName
 */
 getAttributeTagValue(parent, attrName, asNumber) {
 let value = parent.querySelector(`Attribute[Name="${attrName}"] > Value`);
 if (value) {
 if (asNumber && value.textContent) {
 return parseFloat(value.textContent);
 }
 else {
 return value.textContent;
 }
 }
 }
}
window["MtpFileParser"] = MtpFileParser;
//# sourceMappingURL=data:application/json;base64,", "Type" : "BMTypescriptHost", "Visible" : true, "Width" : 144, "Z-index" : 10, "__TypeDisplayName" : "TypeScript Object" }, "Widgets" : [ ] }, { "Properties" : { "Area" : "UI", "Code" : "//#import mtpJsonDef from MTPViewerV2

const NOZZLE_WIDTH = 5;
const NOZZLE_HEIGHT = 5;
const STENCIL_PATH = "/mtp/stencils/";
const STENCIL_LIST = ['agitators.xml', 'apparatus_elements.xml', 'agitators.xml', 'centrifuges.xml', 'compressors.xml',
 'compressors_iso.xml', 'crushers_grinding.xml', 'driers.xml', 'engines.xml', 'feeders.xml', 'filters.xml', 'fittings.xml',
 'flow_sensors.xml', 'heat_exchangers.xml', 'instruments.xml', 'misc.xml', 'mixers.xml', 'piping.xml', 'feeders.xml', 'feeders.xml',
 'pumps.xml', 'pumps_din.xml', 'pumps_iso.xml', 'separators.xml', 'shaping_machines.xml', 'valves.xml', 'vessels.xml'];

/**
 * A MtpFileLoader handles loading a mtp file from the given link, 
 * parsing it into an mxgraph graph object and retring it.
 */
class MtpJsonToMxGraph {
 private mxgraph: any;
 private container: HTMLElement;
 private shapeMap: { [key: string]: string };
 /**
 * Intializes the MtpFileLoader with the mxgraph namespace
 */
 constructor(mxGraphNamespace: any, container: HTMLElement, shapeMap: { [key: string]: string }) {
 this.mxgraph = mxGraphNamespace;
 this.container = container;
 // load all the stencils
 this.loadStencilFiles(STENCIL_LIST.map((el) => { return STENCIL_PATH + el }));
 this.shapeMap = shapeMap;
 }

 public drawMpt(mtpDiagram: HmiDiagram): any {
 try {

 let graph = this.initGraph(new this.mxgraph.mxGraph(this.container));
 // draw the pipes
 this.drawPipes(graph, graph.getDefaultParent(), mtpDiagram.pipes);
 // draw the elements
 this.drawElements(graph, graph.getDefaultParent(), mtpDiagram.elements);

 return graph;
 } catch (e) {
 console.error("Failed to draw mxgraph for MTP")
 throw e;
 }
 }

 private drawElements(graph: any, parent: any, elements: InteractibleElement[]) {
 for (const element of elements) {
 if (element.obj.subElement) {
 // draw the element first (container then the subelement)
 // depending on the rotation of the element we need to offset it across that axis
 let elementOffsetX = element.obj.subElement.height / 2 * Math.abs(Math.sin(element.obj.rotation * Math.PI / 180));
 let elementOffsetY = NOZZLE_HEIGHT * Math.abs(Math.cos(element.obj.rotation * Math.PI / 180));
 graph.insertVertex(parent, element.obj.id, element.obj.name, element.obj.subElement.x - elementOffsetX, element.obj.subElement.y - elementOffsetY,
 element.obj.subElement.width, element.obj.subElement.height, `element;shape=${this.shapeMap[element.obj.eClassClassification]};rotation=${element.obj.rotation}`);
 } else {
 // if we don't have a subelement, draw the visual object directly
 graph.insertVertex(parent, element.obj.id, element.obj.name, element.obj.x, element.obj.y,
 element.obj.width, element.obj.height, `element;shape=${this.shapeMap[element.obj.eClassClassification]}`);
 }
 if (element.communication) {
 // draw the parent first 
 let elParent = graph.insertVertex(parent, element.communication.id, element.communication.name, element.obj.x, element.obj.y,
 element.obj.width, element.obj.height, "swimlane;childLayout=stackLayout;horizontal=1;startSize=26;horizontalStack=0;resizeParent=1;collapsible=1;resizeParentMax=0;resizeLast=0;");
 elParent.collapsed = true;
 // if we have communication elements, draw them in the container
 for (const commInterface of element.communication.interfaces) {
 let value = document.createElement("Value");
 value.setAttribute('label', commInterface.name);
 let cell = graph.insertVertex(elParent, commInterface.id, value, 0, 0, element.obj.width, 20, "interfaceValue");
 }
 }
 }
 }

 private drawPipes(graph: any, parent: any, pipes: PipeDefinition[]) {
 for (const pipe of pipes) {
 // keep a map of the nozzles
 let nozzleMap: { [key: string]: any } = {};
 // draw the nozzles first, then the pipe itself
 for (const nozzle of pipe.nozzles) {
 nozzleMap[nozzle.id] = graph.insertVertex(parent, nozzle.id, nozzle.name,
 nozzle.x - NOZZLE_WIDTH / 2, nozzle.y - NOZZLE_HEIGHT / 2, NOZZLE_WIDTH, NOZZLE_HEIGHT, nozzle.type);
 }
 // draw the pipe itself, but make sure that it's either connected to a nozzle or to a point
 let edge = new this.mxgraph.mxCell(pipe.name, new this.mxgraph.mxGeometry(), pipe.type);
 edge.setId(pipe.id);
 edge.setEdge(true);
 edge.geometry.relative = true;
 // set the waypoints
 edge.geometry.points = pipe.waypoints.map((point) => { return new this.mxgraph.mxPoint(point.x, point.y) });
 // soruce and target point to the vertex or to nothing
 let source = undefined;
 let target = undefined;
 if (isNozzle(pipe.source)) {
 source = nozzleMap[pipe.source.id];
 } else {
 // set the starting point a a point
 edge.geometry.sourcePoint = new this.mxgraph.mxPoint(pipe.source.x, pipe.source.y);
 }
 if (isNozzle(pipe.target)) {
 target = nozzleMap[pipe.target.id];
 } else {
 // set the target point a a point
 edge.geometry.targetPoint = new this.mxgraph.mxPoint(pipe.target.x, pipe.target.y);
 }
 graph.addEdge(edge, parent, source, target);
 }
 }

 public initGraph(graph: any): any {
 // Allow panning using the right click buttion
 graph.setPanning(true);
 // enable the display of tooltips
 graph.setTooltips(true);
 // disable new connections and cloning cells, as well as drag and drop outside
 graph.setConnectable(false);
 graph.setCellsCloneable(true);
 graph.setCellsDeletable(true);
 graph.setDropEnabled(true);
 graph.setSplitEnabled(false);
 graph.resetEdgesOnConnect = false;

 this.declareMxGraphStyles(graph);
 graph.getModel().prefix = "autoGenerated";

 graph.getLabel = function (cell: any) {
 if (cell) {
 // get the name of style
 let cellStyle = cell.style ? (cell.style.split(";") ? cell.style.split(";")[0] : cell.style) : undefined;
 if (cellStyle == "interfaceValue") {
 return `${cell.getAttribute("label")}: ${cell.getAttribute("value", "N/A")}`;
 } else if (cellStyle == "Nozzle") {
 return "";
 } else if (cellStyle == "element") {
 return "";
 } else if (cell.edge) {
 return ""
 } else {
 return cell.value;
 }
 }
 }

 let layout = new this.mxgraph.mxStackLayout(graph, true);
 layout.resizeParent = true;
 layout.horizontal = false;
 layout.spacing = 2;
 let layoutMgr = new this.mxgraph.mxLayoutManager(graph);
 layoutMgr.getLayout = function (cell: any) {
 let cellStyle = cell.style ? (cell.style.split(";") ? cell.style.split(";")[0] : cell.style) : undefined;
 if (cellStyle == "swimlane") {
 return layout;
 }

 return null;
 };

 return graph;
 }

 /**
 * Creates the generic styles for elements
 */
 private declareMxGraphStyles(graph: any) {
 let style = graph.getStylesheet().getDefaultVertexStyle();
 style[this.mxgraph.mxConstants.STYLE_ROUNDED] = false;
 style[this.mxgraph.mxConstants.STYLE_FONTCOLOR] = '#1d1b1b';
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = '#9673A6';
 style[this.mxgraph.mxConstants.STYLE_FILLCOLOR] = '#f3f4f9';


 style = this.mxgraph.mxUtils.clone(style);
 style[this.mxgraph.mxConstants.STYLE_SHAPE] = 'swimlane';
 style[this.mxgraph.mxConstants.STYLE_STARTSIZE] = 20;
 graph.getStylesheet().putCellStyle('swimlane', style);

 style = [];
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = 'red';
 style[this.mxgraph.mxConstants.STYLE_SHAPE] = "ellipse";
 graph.getStylesheet().putCellStyle('Nozzle', style);

 style = [];
 style[this.mxgraph.mxConstants.STYLE_FILLCOLOR] = 'none';
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = 'none';
 graph.getStylesheet().putCellStyle('interfaceValue', style);

 style = graph.getStylesheet().getDefaultEdgeStyle();
 style[this.mxgraph.mxConstants.STYLE_ENDARROW] = 'none';
 style[this.mxgraph.mxConstants.STYLE_STROKEWIDTH] = 2;
 }
 /**
 * Loads the stencil files for pid diagrams
 */
 private loadStencilFiles(files: string[]) {
 for (const filePath of files) {
 let req = this.mxgraph.mxUtils.load(filePath);
 let root = req.getDocumentElement();
 let prefix = root.getAttribute("name");
 let shape = root.firstChild;

 while (shape != null) {
 if (shape.nodeType == this.mxgraph.mxConstants.NODETYPE_ELEMENT) {
 let name = prefix + '.' + shape.getAttribute('name').replace(/ /g, '_');
 this.mxgraph.mxStencilRegistry.addStencil(name.toLowerCase(), new this.mxgraph.mxStencil(shape));
 }
 shape = shape.nextSibling;
 }
 }
 }
}
