 c.moveTo(w * 0.21, h * 0.32);
 c.lineTo(w * 0.79, h * 0.20);
 c.moveTo(w * 0.1, h * 0.52);
 c.lineTo(w * 0.9, h * 0.36);
 c.moveTo(0, h * 0.72);
 c.lineTo(w, h * 0.5);
 c.stroke();
 };

 mxShapePidValve.prototype.drawSolenoidManResetAct = function (c, w, h) {
