/**
 * Utility class for easier working with points
 */
interface mxPoint {
 x: number;
 y: number;
}
/**
 * A MtpFileLoader handles loading a mtp file from the given link, 
 * parsing it into an mxgraph graph object and retring it.
 */
class MptFileLoader {
 private mxgraph: any;
 private container: HTMLElement;
 private shapeMap: {[key: string]: string};
 /**
 * Intializes the MtpFileLoader with the mxgraph namespace
 */
 constructor(mxGraphNamespace: any, container: HTMLElement, shapeMap) {
 this.mxgraph = mxGraphNamespace;
 this.mxgraph.mxPoint.fromString = (value: string) => {
 const tokens = value.split(",");
 if (tokens.length < 2) {
 throw "We need two points to create a mxPoint"
 }
 return new this.mxgraph.mxPoint(parseFloat(tokens[0]), parseFloat(tokens[1]));
 }
 this.container = container;
 // load all the stencils
 this.loadStencilFiles(STENCIL_LIST.map((el) => {return STENCIL_PATH + el}));
 this.shapeMap = shapeMap;
 }

 public async loadFile(filePath: string): Promise<any> {
 try {
 const response = await fetch(filePath);
 const responseText = await response.text();
 const xmlData = new DOMParser().parseFromString(responseText, "text/xml");
 return this.parseMtpXml(xmlData);
 } catch (e) {
 console.error("Failed to load MTP")
 throw e;
 }
 }

 private parseMtpXml(file: Document): any {
 // we need to iterate through the file and find a InstanceHierarchy with the name HMI
 const hmiDiagram = file.querySelector('InstanceHierarchy[Name="HMI"] InternalElement[Name="Visu_Main"]');

 if (!hmiDiagram) {
 throw "No HMI found";
 }

 // start creating the diagram
 let graph = new this.mxgraph.mxGraph(this.container);
 graph.foldingEnabled = false;
 // Allow panning using the right click buttion
 graph.setPanning(true);
 // enable the display of tooltips
 graph.setTooltips(true);
 // disable new connections and cloning cells, as well as drag and drop outside
 graph.setConnectable(false);
 graph.setCellsCloneable(false);
 graph.setCellsDeletable(false);
 graph.setDropEnabled(false);
 graph.setSplitEnabled(false);

 this.declareMxGraphStyles(graph);
 let parent = graph.getDefaultParent();
 // add the visualization
 //parent = graph.insertVertex(parent, null, null, 0, 0, this.getAttributeValue(hmiDiagram, "Width"),
 // this.getAttributeValue(hmiDiagram, "Height"), 'parent');


 graph.getModel().prefix = "autoGenerated";

 // iterate through the internal elements of the diagram and add them to the diagram
 for (let i = 0; i < hmiDiagram!.childElementCount; i++) {
 const element = hmiDiagram!.children[i] as Element;
 if (element.nodeName == "InternalElement") {
 if (this.getAttributeValue(element, "Edgepath", false)) {
 // if there is a attribute with an edge path, assume it's a line
 // first find the internal elements, most likely Nozzles
 this.handleInternalNozzles(element, graph, parent);
 // now add the pipe element
 let edgePath = this.getAttributeValue(element, "Edgepath", false);
 let points = this.parseEdgePath(edgePath as string);
 let edge = this.createPipe(element.getAttribute("ID")!, element.getAttribute("Name")!, "pipe",
 points.start, points.end, points.waypoints);

 graph.addEdge(edge, parent);
 } else if (this.getAttributeValue(element, "eClassClassificationClass")) {
 // we have a eClassClassificationClass, so this is most likely a diagram element
 let parsedPosition = {
 x: this.getAttributeValue(element, "X") as number,
 y: this.getAttributeValue(element, "Y") as number,
 width: this.getAttributeValue(element, "Width") as number,
 height: this.getAttributeValue(element, "Height") as number
 };
 // we create the parent for this element first. this is used as an container for all the components
 let elementParent = graph.insertVertex(parent, element.getAttribute("ID"), element.getAttribute("Name"),
 parsedPosition.x, parsedPosition.y, parsedPosition.width, parsedPosition.height,
 `parent;rotation=${this.getAttributeValue(element, "Rotation")}`);
 graph.insertVertex(parent, element.getAttribute("ID"), element.getAttribute("Name"),
 parsedPosition.x, parsedPosition.y, parsedPosition.width, parsedPosition.height,
 `element;shape=${this.shapeMap[this.getAttributeValue(element, "eClassClassificationClass") as string]};`+
 `rotation=${this.getAttributeValue(element, "Rotation")}`);
 this.handleInternalNozzles(element, graph, parent);
 }
 }

 }

 return graph;
 }

 /**
 * Creates nozzles for the given element
 */
 private handleInternalNozzles(element: Element, graph: any, parent: any) {
 for (let j = 0; j < element.childElementCount; j++) {
 const subElement = element.children[j] as Element;
 if (subElement.nodeName == "InternalElement") {
 // find the x and y
 let xPosition = this.getAttributeValue(subElement, "X") as number;
 let yPosition = this.getAttributeValue(subElement, "Y") as number;
 // add the nozzle to the graph
 graph.insertVertex(parent, subElement.getAttribute("ID"), undefined,
 xPosition - NOZZLE_WIDTH/2, yPosition - NOZZLE_HEIGHT/2, NOZZLE_WIDTH, NOZZLE_HEIGHT, "nozzle");
 }
 }
 }
 /**
 * Computes a list of points into a start, end and intermidiate points
 */
 private parseEdgePath(edgePath: string): { start: mxPoint, end: mxPoint, waypoints: mxPoint[] } {
 let tokens = edgePath.split(";");
 return {
 start: this.mxgraph.mxPoint.fromString(tokens[0]),
 end: this.mxgraph.mxPoint.fromString(tokens[tokens.length - 1]),
 waypoints: tokens.slice(1, -1).map((el) => { return this.mxgraph.mxPoint.fromString(el) })
 }
 }

 /**
 * XML parsing fucntion, helping getting the Value tag of a Attribute tag identified by the attrName
 */
 private getAttributeValue(parent: Element, attrName: string, asNumber = true) {
 let value = parent.querySelector(`Attribute[Name="${attrName}"] > Value`);
 if (value) {
 if (asNumber && value.textContent) {
 return parseFloat(value.textContent);
 } else {
 return value.textContent;
 }
 }
 }

 /**
 * Creates a pipe with the given attributes
 */
 private createPipe(id: string, value: string, style: string, sourcePoint: mxPoint, targetPoint: mxPoint, waypoints: mxPoint[]) {
 let edge = new this.mxgraph.mxCell(value, new this.mxgraph.mxGeometry(), style);
 edge.setId(id);
 edge.setEdge(true);
 edge.geometry.relative = true;
 // set the starting and ending poitns
 edge.geometry.sourcePoint = sourcePoint;
 edge.geometry.targetPoint = targetPoint;
 edge.geometry.points = waypoints;
 return edge;
 }

 /**
 * Creates the generic styles for elements
 */
 private declareMxGraphStyles(graph: any) {
 var style = graph.getStylesheet().getDefaultVertexStyle();
 style[this.mxgraph.mxConstants.STYLE_ROUNDED] = false;
 style[this.mxgraph.mxConstants.STYLE_FONTCOLOR] = '#1d1b1b';
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = '#9673A6';
 style[this.mxgraph.mxConstants.STYLE_FILLCOLOR] = '#f3f4f9';

 style = this.mxgraph.mxUtils.clone(style);
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = 'red';
 style[this.mxgraph.mxConstants.STYLE_SHAPE] = "ellipse";
 graph.getStylesheet().putCellStyle('nozzle', style);

 style = this.mxgraph.mxUtils.clone(style);
 style[this.mxgraph.mxConstants.STYLE_FILLCOLOR] = 'transparent';
 style[this.mxgraph.mxConstants.STYLE_STROKECOLOR] = 'transparent';
 style[this.mxgraph.mxConstants.STYLE_SHAPE] = this.mxgraph.mxConstants.SHAPE_RECTANGLE;
 style[this.mxgraph.mxConstants.STYLE_PERIMETER] = this.mxgraph.mxConstants.PERIMETER_RECTANGLE;
 graph.getStylesheet().putCellStyle('parent', style);

 var style = graph.getStylesheet().getDefaultEdgeStyle();
 style[this.mxgraph.mxConstants.STYLE_ENDARROW] = 'none';
 style[this.mxgraph.mxConstants.STYLE_STROKEWIDTH] = 2;
 }
 /**
 * Loads the stencil files for pid diagrams
 */
 private loadStencilFiles(files: string[]) {
 for (const filePath of files) {
 var req = this.mxgraph.mxUtils.load(filePath);
 var root = req.getDocumentElement();
 var prefix = root.getAttribute("name");
 var shape = root.firstChild;

 while (shape != null) {
 if (shape.nodeType == this.mxgraph.mxConstants.NODETYPE_ELEMENT) {
 var name = prefix + '.' + shape.getAttribute('name').replace(/ /g, '_');
 this.mxgraph.mxStencilRegistry.addStencil(name.toLowerCase(), new this.mxgraph.mxStencil(shape));
 }
 shape = shape.nextSibling;
 }
 }
 }
}
